import React, { useEffect, useState } from "react";
import axios from "axios";
import iitjee from "../../Images/iit-jee-course.jpg";
import { Link, useParams } from "react-router-dom";

const CoursePage = () => {
 
    const [courseCard, setCourseCard] = useState([]);
  const examId = useParams();
  useEffect(() => {
    axios
      .get(`http://localhost:4009/feachingcourse/${examId}`)
      .then((response) => {
        setCourseCard(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [examId]);

  return (
    <div>
      <h1>Current Courses</h1>
      <ul className="card_container_ul">
      {courseCard.map((courseDetails) => (
          <React.Fragment key={courseDetails.courseCreationId}>
            <li>
             {courseDetails.courseName}
            </li>
            <li>
              Course Start Date: {courseDetails.courseStartDate}
            </li>
            <li>
              Course End Date: {courseDetails.courseEndDate}
            </li>
            <li>{course.cost}</li>
            <li>{course.Discount}%</li>
            <li>Price after discount:{course.totalPrice}</li>
            <li>
              <Link to={`/feachingtest/${course.courseCreationId}`}>
                Test Page
              </Link> </li>
          </React.Fragment>
        ))}
                  {courseCard.map((courseDetails) => (
                    <React.Fragment key={courseDetails.examId}>
                      <div className="card_container_li">
                        <img src={iitjee} alt="card" width={350} />
                        <li>{courseDetails.examName}</li>
                        <li>
                          {" "}
                          Validity: ({courseDetails.startDate}) to (
                          {courseDetails.endDate})
                        </li><li>
                      <div className="start_now">
                          <Link to={`/CoursePage/${courseDetails.examId}`}>Start Now </Link>
                        </div>
                        {/* <Link to={`/feachingcourse/${cardItem.examId}`}>
                          View Courses for {cardItem.examName}
                        </Link> */}
                      </li>
                      </div>

                      
                    </React.Fragment>
                  ))}
                </ul>
       
    </div>
  )
}

export default CoursePage
