                <form action="">
                    {/* user name-------- */}
                    <div className="reg_contant">
                        <input type="text"
                            placeholder='Name'
                            name='name'
                            className='reg_input'
                            onChange={handleInputChange}
                        />
                        <span></span>
                    </div>

                    {/* user  email Address-------- */}
                    <div className="reg_contant">
                        <input type="text"
                            placeholder=' Email Address'
                            name=' emailAddress'
                            className='reg_input'
                            onChange={handleInputChange}
                        />
