                            <option value="Andhra Pradesh">Andhra Pradesh</option>
                        </select>
                        <span></span>
                    </div>

                     {/* user password-------- */}
                     <div className="reg_contant">
                        <select name="city" id="" className='reg_input'  onChange={handleInputChange}>
                            <option value="Andaman and Nicobar Islands">Andaman and Nicobar Islands</option>
                            <option value="Andhra Pradesh">Andhra Pradesh</option>
                        </select>
                        <span></span>
                    </div>

                     {/* user password-------- */}
                     <div className="reg_contant">
                        <select name="course" id="" className='reg_input'  onChange={handleInputChange}>
                            <option value="Andaman and Nicobar Islands">Andaman and Nicobar Islands</option>
                            <option value="Andhra Pradesh">Andhra Pradesh</option>
                        </select>
                        <span></span>
                    </div>

                </form>
            </div>
        </div>
    )
}
