  const [subjectName, setSubjectName] = useState([]);
  useEffect(() => {
    // Fetch data from the endpoint
    fetch(`http://localhost:4009/quiz_all/` + subi_id)
      .then((response) => response.json())
      .then((data) => setSubjectName(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, [subi_id]);

  return (
    <div>
      <h1>hii</h1>
      <div>
        {subjectName.map((subject) => (
          <li key={subject.subi_id}>
            <button>{subject.subject_name}</button>
            {/* <img src={`data:image/png;base64,${subject.subject_name}`} alt={`Option ${option.option_id}`} /> */}
          </li>
        ))}
        {/* <button>Mathematics</button>
                <button>Physics</button>
                <button>Chemistry</button> */}
      </div>
      {/* <h1>Quiz Questions</h1> */}
      <div>
        {quizData.map((question) => (
          <div key={question.question_id}>
            <h3>Question {question.question_id}</h3>
            <img
              src={`data:image/png;base64,${question.question_img}`}
              alt={`Question ${question.question_id}`}
            />
            <ul>
              {question.options.map((option) => (
                <li key={option.option_id}>
                  <img
                    src={`data:image/png;base64,${option.option_img}`}
                    alt={`Option ${option.option_id}`}
                  />
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Paper1;
