import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import PaperHeader from "../../Components/PaperHeader/PaperHeader";

const Paper1 = () => {
  const [questionData, setQuestionData] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    // Fetch all images from the server
    fetch("http://localhost:4009/getAllImages")
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setQuestionData(data);
        } else {
          console.error(
            "Invalid data structure received from the server:",
            data
          );
        }
      })
      .catch((error) => console.error("Error fetching images:", error));
  }, []);

  const handleNextClick = () => {
    // Update the current question index to move to the next question
    setCurrentQuestionIndex((prevIndex) =>
      prevIndex < questionData.length - 1 ? prevIndex + 1 : prevIndex
    );
  };

  const [Subjects, setSubjects] = useState([]);
  const { testCreationTableId } = useParams();

  useEffect(() => {
    const fetchSubjects = async () => {
      try {
        const response = await fetch(
          `http://localhost:4009/subjects/${testCreationTableId}`
        );
        const data = await response.json();
        setSubjects(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchSubjects();
  }, [testCreationTableId]);

  const [questionStatus, setQuestionStatus] = useState(
    Array(questionData.length).fill("notVisited")
  );
  const [selectedAnswers, setSelectedAnswers] = useState(
    Array(questionData.length).fill("")
  );
  const [activeQuestion, setActiveQuestion] = useState(0);

  const onAnswerSelected = (optionValue) => {
    const updatedSelectedAnswers = [...selectedAnswers];
    updatedSelectedAnswers[activeQuestion] = optionValue;
    setSelectedAnswers(updatedSelectedAnswers);
  
    const updatedQuestionStatus = [...questionStatus];
    updatedQuestionStatus[activeQuestion] = "answered";
    setQuestionStatus(updatedQuestionStatus);
  };

 
    // Assuming optionValue is a property of your option object
    const optionValue = image.optionValue;

  return (
    <div>
      <div>
        <PaperHeader />
      </div>
      <div>
        {Subjects.map((subjectTitle, index) => (
          <li key={index}>
            <Link>{subjectTitle.subjectName}</Link>
          </li>
        ))}
      </div>
      {questionData.length > 0 && (
        <div>
          <h4>
            {" "}
            {currentQuestionIndex + 1}.
            <img
              src={questionData[currentQuestionIndex].questionImage}
              alt={`Question ${currentQuestionIndex + 1}`}
            />
          </h4>

          {questionData[currentQuestionIndex].optionImages.map(
          
            (image, optionIndex) => (
              <li key={optionIndex}>
                   const optionValue = image.optionValue;
                <input
                  type="radio"
                  name={`question-${currentQuestionIndex}`}
                  checked={
                    selectedAnswers[currentQuestionIndex] === optionValue
                  }
                  onChange={() => onAnswerSelected(optionValue)}
                />
                <img
                  key={optionIndex}
                  src={image}
                  alt={`Option ${optionIndex + 1}`}
                />
              </li>
            )
          )}

          <button onClick={handleNextClick}>Next</button>
        </div>
      )}
    </div>
  );
};

export default Paper1;
